    setSelectedCategory(""); // 검색 조건이 설정되면 카테고리 초기화
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category); // 선택된 카테고리 설정
    setSearchData(null); // 카테고리 선택 시 검색 조건 초기화
  };

  return (
    <Router>
      <div className="app-container">
        <Navbar/>
        <Header/>
        <Routes>
          <Route path="/"
            element={
              <>
                <CategoryMenu
