import React, { useState, useEffect } from "react";
import { useNavigate} from "react-router-dom";
import "./../styles/OnlyReporters.css";

function onlyReporters() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // 로그인 상태
  const [userRole, setUserRole] = useState(""); // 사용자 권한
  const navigate = useNavigate();


  const onLogin = (role) => {
    setIsLoggedIn(true);
    setUserRole(role);
  };

  const onLogout = () => {
    setIsLoggedIn(false);
    setUserRole(null);
    localStorage.removeItem("token");
    localStorage.removeItem("role");
  };

  // 로그인 상태 복원 로직
  useEffect(() => {
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role");

    if (token && role) {
      setIsLoggedIn(true);
      setUserRole(role);
    }
  }, []);

  return (
    <div className="reporters-container">
      <h1>Reporter Access</h1> 
      <div className="reporters-buttons">
        {!isLoggedIn ? (
          //로그인 안 된 경우
          <>
            <button className="register-btn" onClick={() => navigate("/register")}>회원가입</button>
            <button className="login-btn" onClick={() => navigate("/login")}>로그인</button>
          </>
        ) : (
          //로그인 된 경우
          <>
            <button className="login-btn" onClick={onLogout}>로그아웃</button>
            {userRole === "User" && (<button className="requestRole-btn" onClick={() => navigate("/login")}>포스팅 권한 요청</button>)}
            {userRole === "Reporter" && (<button className="posting-btn" onClick={() => {navigate("/post")}}>기사 포스팅팅</button>)}
          </>
        )}
      </div>
    </div>
  );
}

export default onlyReporters;
