import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./../styles/PostForm.css";

//게시글 작성
function PostForm() {
  const navigate = useNavigate();
  const [form, setForm] = useState({title: "", category: "", content: "", issue: "", page: "", previewSentence: "", thumbnailImage: "" });
  const editorRef = useRef(null); // CKEditor 인스턴스 참조

  useEffect(() => {
    if (document.getElementById("editor")) {
      const editor = window.CKEDITOR.replace("editor", {
        height: 300,
        extraPlugins: "uploadimage",
        filebrowserUploadMethod: "form",
        filebrowserUploadUrl: "http://localhost:7000/api/posts/postImages",
      });

      editor.on("fileUploadRequest", function (evt) {
        const fileLoader = evt.data.fileLoader;
        const formData = new FormData();
        formData.append("upload", fileLoader.file); // 요청할 때 "upload"라는 키 사용

        const xhr = fileLoader.xhr;
        xhr.open("POST", "http://localhost:7000/api/posts/postImages", true);
        xhr.setRequestHeader("Authorization", `Bearer ${localStorage.getItem("token")}`);
        xhr.send(formData);

        evt.stop();
      });

      editorRef.current = editor;
    }
  }, []);

  const handleSubmit = async () => {
    const content = editorRef.current?.getData(); // CKEditor 내용 가져오기
    try {
      const response = await fetch("http://localhost:7000/api/posts", {
        method: "POST", // 새 글 작성 요청
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ ...form, content }),
      });
      const data = await response.json();
      if (response.ok) {
        alert("게시글 작성이 완료되었습니다.");
        navigate("/"); // 작성 또는 수정 후 홈으로 이동
      } else {
        alert(data.message || "게시글 제출 오류");
      }
    } catch (err) {
      console.error("게시글 제출 오류: ", err);
    }
  };

  return (
    <div className="post-form-container">
      <h1>Post Article</h1>
      <div>
        <label>Title</label>
        <input
          type="text"
          placeholder="기사 제목(편집일 최종본)"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
      </div>
      <div>
        <label>Issue</label>
        <input
          type="text"
          placeholder="몇 호인지 숫자만 입력 (예: 173)"
          value={form.issue}
          onChange={(e) => setForm({ ...form, issue: e.target.value })}
        />
      </div>
      <div>
        <label>Category</label>
        <select
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
        >
          <option>On Campus</option>
          <option>Feature</option>
          <option>Issue</option>
          <option>Society</option>
          <option>Global</option>
          <option>Culture</option>
          <option>Entertainment</option>
          <option>Economics</option>
          <option>Business</option>
          <option>Technology</option>
        </select>

        <label>Page (예: 3면(1)이면 31 선택)</label>
        <select
          value={form.page}
          onChange={(e) => setForm({ ...form, page: e.target.value })}
        >
          <option>11</option>
          <option>12</option>
          <option>13</option>
          <option>14</option>
          <option>21</option>
          <option>22</option>
          <option>23</option>
          <option>24</option>
          <option>25</option>
          <option>26</option>
          <option>31</option>
          <option>32</option>
          <option>33</option>
          <option>34</option>
          <option>35</option>
          <option>36</option>
          <option>41</option>
          <option>42</option>
          <option>43</option>
          <option>44</option>
          <option>51</option>
          <option>52</option>
          <option>53</option>
          <option>54</option>
          <option>61</option>
          <option>62</option>
          <option>63</option>
          <option>64</option>
          <option>71</option>
          <option>72</option>
          <option>73</option>
          <option>74</option>
          <option>81</option>
          <option>82</option>
          <option>83</option>
          <option>84</option>
        </select>
      </div>
      <div>
        <label>1 Sentence for preview</label>
        <input
          type="text"
          placeholder="(예: Samsung Electronics’ stock price, which stood at 83,100 won on August 1, ...) "
          value={form.previewSentence}
          onChange={(e) => setForm({ ...form, previewSentence: e.target.value })}
        />
      </div>
      <div>
        <label>1 Thumbnail Image Link</label>
        <input
          type="text"
          placeholder="에디터로 사진 업로드 후 링크 복사 붙여넣기"
          value={form.thumbnailImage}
          onChange={(e) => setForm({ ...form, thumbnailImage: e.target.value })}
        />
      </div>
      <div>
        <label>Content</label>
        <textarea
          id="editor"
          value={form.content}  // value로 form.content를 설정
          onChange={(e) => setForm({ ...form, content: e.target.value })} // onChange로 상태 관리
        />
      </div>
      <button onClick={handleSubmit} className="submit-button">
        Submit
      </button>
    </div>
  );
}

export default PostForm;
