import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./../styles/PostForm.css";

//Í≤åÏãúÍ∏Ä ÏûëÏÑ±
function PostForm() {
  const navigate = useNavigate();
  const editorRef = useRef(null); // CKEditor Ïù∏Ïä§ÌÑ¥Ïä§ Ï∞∏Ï°∞
  const [userRole, setUserRole] = useState("");
  const [form, setForm] = useState({
    title: "", 
    category: "", 
    content: "", 
    issue: "", 
    publicationDate: "",
    page: "", 
    previewSentence: "", 
    thumbnailImage: "" ,
  });

  useEffect(() => {

    const role = localStorage.getItem("role");
    if (!role || role !== "Reporter") {
      alert("ÏÜåÏÜç Í∏∞ÏûêÎßå Í∏ÄÏùÑ Í≤åÏãúÌï† Ïàò ÏûàÏäµÎãàÎã§.");
      navigate("/"); // Í∂åÌïú ÏóÜÏúºÎ©¥ ÌôàÏúºÎ°ú Ïù¥Îèô
    } else {
      setUserRole(role);
    }

    // CKEditor Ï¥àÍ∏∞Ìôî
    if (document.getElementById("editor")) {
      window.CKEDITOR.replace("editor", {
        height: 300,
        filebrowserUploadUrl: "http://localhost:7000/api/posts/postImages",
        filebrowserUploadMethod: "form",
        extraPlugins: "uploadimage", // Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú ÌîåÎü¨Í∑∏Ïù∏ ÌôúÏÑ±Ìôî
        filebrowserUploadMethod: "xhr", // iframe ÎåÄÏã† XHRÏùÑ ÏÇ¨Ïö©
      });

      // CKEditor ÏóÖÎ°úÎìú ÌõÑ URL ÌëúÏãúÎ•º ÏúÑÌïú Ïù¥Î≤§Ìä∏ Ï∂îÍ∞Ä
      window.CKEDITOR.instances.editor.on("fileUploadResponse", function (evt) {
        const xhr = evt.data.fileLoader.xhr;
        const response = JSON.parse(xhr.responseText);

        if (response.uploaded) {
          console.log("Uploaded URL:", response.url); // URL ÌôïÏù∏Ïö© Î°úÍ∑∏
          evt.data.fileLoader.fileName = response.url; // ÏóÖÎ°úÎìúÎêú URLÏùÑ CKEditorÏóê ÏûêÎèô ÏÇΩÏûÖ
        } else {
          console.error("Upload failed:", response.error); // Ïã§Ìå® Î°úÍ∑∏
        }
      });

      editorRef.current = window.CKEDITOR.instances.editor;
    }
    return () => {
      if (editorRef.current) {
        editorRef.current.destroy(true); // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú CKEditor Ïù∏Ïä§ÌÑ¥Ïä§ Ï†úÍ±∞
      }
    };
  }, []);

  const handleSubmit = async () => {
    // Get the content from CKEditor
    const content = editorRef.current?.getData(); 

    if (!content) {
      alert("ÎÇ¥Ïö©ÏùÄ ÌïÑÏàò ÏûÖÎ†• Ìï≠Î™©ÏûÖÎãàÎã§.");
      return;
    }

    // Check if issue and page are not empty and convert them to numbers
    const issueNumber = parseInt(form.issue, 10);
    const pageNumber = parseInt(form.page, 10);
    const publicationDateNumber = parseInt(form.publicationDate, 10);

    // Validate if issue and page are valid numbers
    if (isNaN(issueNumber) || isNaN(publicationDateNumber) || isNaN(pageNumber)) {
      alert("Issue No., Publication Date, Page Ìï≠Î™©ÏùÄ Ïà´ÏûêÏó¨Ïïº Ìï©ÎãàÎã§.");
      return;
    }

    if (!form.category || !form.title) {
      alert("Ï†úÎ™©Í≥º Ïπ¥ÌÖåÍ≥†Î¶¨Îäî ÌïÑÏàò ÏûÖÎ†• Ìï≠Î™©ÏûÖÎãàÎã§.");
      return;
    }

    try {
      const response = await fetch("http://localhost:7000/api/posts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          title: form.title,
          category: form.category,
          content,
          issue: issueNumber,  // Send issue as number
          publicationDate: publicationDateNumber, // Send publicationDate as number
          page: pageNumber,    // Send page as number
          previewSentence: form.previewSentence,
          thumbnailImage: form.thumbnailImage,
        }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("Í∏∞ÏÇ¨Í∞Ä Í≤åÏãúÎêòÏóàÏäµÎãàÎã§.");
        navigate("/"); // Redirect after successful post creation
      } else {
        alert(data.message || "Í∏∞ÏÇ¨ Îì±Î°ù Ïò§Î•ò");
      }
    } catch (err) {
      console.error("Í∏∞ÏÇ¨ Îì±Î°ù Ïò§Î•ò:", err);
    }
  };


  return (
    <div className="post-form-container">
      <h1>Post Article</h1>
      <div>
        <label>Title</label>
        <input
          type="text"
          placeholder="Í∏∞ÏÇ¨ Ï†úÎ™© (Ìé∏ÏßëÏùº ÏµúÏ¢ÖÎ≥∏)"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
      </div>
      <div>
        <label>Issue No.</label>
        <input
          type="text"
          placeholder="Î™á Ìò∏Ïù∏ÏßÄ Ïà´ÏûêÎßå ÏûÖÎ†• (Ïòà: 173)"
          value={form.issue}
          onChange={(e) => setForm({ ...form, issue: e.target.value })}
        />
      </div>
      <div>
        <label>Publication Date</label>
        <input
          type="text"
          placeholder="Î∞úÍ∞ÑÏùº Ïà´ÏûêÎßå ÏûÖÎ†• (Ïòà: 241209)"
          value={form.publicationDate}
          onChange={(e) => setForm({ ...form, publicationDate: e.target.value })}
        />
      </div>
      <div>
        <label>Category</label>
        <select
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
        >
          <option>(Ïòà: 1Î©¥, Î≥¥ÎèÑÍ∏∞ÏÇ¨Îäî On Campus ÏÑ†ÌÉù)</option>
          <option>On Campus</option>
          <option>Feature</option>
          <option>Issue</option>
          <option>Society</option>
          <option>Global</option>
          <option>Culture</option>
          <option>Entertainment</option>
          <option>Economics</option>
          <option>Business</option>
          <option>Technology</option>
        </select>

        <label>Page</label>
        <select
          value={form.page}
          onChange={(e) => setForm({ ...form, page: e.target.value })}
        >
          <option>(Ïòà: 3Î©¥(1)Ïù¥Î©¥ 31 ÏÑ†ÌÉù, 1Î©¥Ïù¥Î©¥ 11 ÏÑ†ÌÉù)</option>
          <option>11</option>
          <option>12</option>
          <option>13</option>
          <option>14</option>
          <option>21</option>
          <option>22</option>
          <option>23</option>
          <option>24</option>
          <option>25</option>
          <option>26</option>
          <option>31</option>
          <option>32</option>
          <option>33</option>
          <option>34</option>
          <option>35</option>
          <option>36</option>
          <option>41</option>
          <option>42</option>
          <option>43</option>
          <option>44</option>
          <option>51</option>
          <option>52</option>
          <option>53</option>
          <option>54</option>
          <option>61</option>
          <option>62</option>
          <option>63</option>
          <option>64</option>
          <option>71</option>
          <option>72</option>
          <option>73</option>
          <option>74</option>
          <option>81</option>
          <option>82</option>
          <option>83</option>
          <option>84</option>
        </select>
      </div>
      <div>
        <label>1 sentence for preview</label>
        <input
          type="text"
          placeholder="(Ïòà: Samsung Electronics‚Äô stock price, which stood at 83,100 won on August 1, plunged by nearly 40 percent to 49,900 won by November 14‚Äîthe lowest price since May 2020.) "
          value={form.previewSentence}
          onChange={(e) => setForm({ ...form, previewSentence: e.target.value })}
        />
      </div>
      <div>
        <label>1 thumbnail Image Link</label>
        <input
          type="text"
          placeholder="ÏóêÎîîÌÑ∞Î°ú ÏÇ¨ÏßÑ ÏóÖÎ°úÎìú ÌõÑ ÎßÅÌÅ¨ Î≥µÏÇ¨ Î∂ôÏó¨ÎÑ£Í∏∞ (Ïòà: https://theajouglobe.s3.ap-northeast-2.amazonaws...jpg)"
          value={form.thumbnailImage}
          onChange={(e) => setForm({ ...form, thumbnailImage: e.target.value })}
        />
      </div>
      <div>
      <p className="explanation">
          ‚ÄªÏÇ¨ÏßÑ ÏóÖÎ°úÎìú Í∑úÏπô‚Äª<br></br>
          üíöÏµúÏÜåÌôîÏßàÎ°ú Î≥¥ÎèÑÏÇ¨ÏßÑ ÏïïÏ∂ï: 
            <a href="https://www.iloveimg.com/ko/compress-image" target="_blank">  iloveimg  </a><br></br>
          üíöÏÇ¨ÏßÑÎ™Ö - Ïà´ÏûêÎßå! Ïòà) 173Ìò∏ 3Î©¥(1) Î≥¥ÎèÑÏÇ¨ÏßÑ1 ‚ñ∂ "173_3(1)_1"<br></br>
          üíöÏõêÌïòÎäî ÏúÑÏπòÏóê Í∞§Îü¨Î¶¨ ÏïÑÏù¥ÏΩò ÌÅ¥Î¶≠Ìï¥ÏÑú "ÏóÖÎ°úÎìú Î©îÎâ¥"‚ñ∂"ÌååÏùº ÏÑ†ÌÉù", ÏÑúÎ≤ÑÎ°ú Ï†ÑÏÜ°" ÌÅ¥Î¶≠‚ñ∂Ïù¥ÎØ∏ÏßÄ ÎÑàÎπÑ 380ÏûÖÎ†•‚ñ∂ÌôïÏù∏ ÌÅ¥Î¶≠<br></br>
          üíöÏÇ¨ÏßÑÏÑ§Î™Ö - Í∏ÄÏî®Ï≤¥: Í∏∞Î≥∏, Í∏ÄÏî®ÌÅ¨Í∏∞: 9 pt, Ï†ïÎ†¨: ÏôºÏ™Ω, Í∏ÄÏî®Ïä§ÌÉÄÏùº: Î≥ºÎìú<br></br><br></br>
          ‚ÄªÌÖçÏä§Ìä∏ Í∑úÏπô‚Äª<br></br>
          üíöÍ∏∞ÏÇ¨ÎÇ¥Ïö© - Í∏ÄÏî®Ï≤¥: times new roman, Í∏ÄÏî®ÌÅ¨Í∏∞: Í∏∞Î≥∏, Ï†ïÎ†¨: ÏñëÏ™Ω<br></br>
          üíöÍ∏∞ÏûêÎ™Ö - Ìïú Ï§Ñ ÎùÑÍ≥†, Í∏ÄÏî®Ï≤¥: Í∏∞Î≥∏, Í∏ÄÏî®ÌÅ¨Í∏∞: Í∏∞Î≥∏, Ï†ïÎ†¨: Ïò§Î•∏Ï™Ω<br></br>
          üíöÍ∑∏ Ïô∏ Í∞ÅÏ£º, Ïù¥ÌÉ§Î¶≠, Î≥ºÎìú Îì± Îã§Îì¨Í∏∞<br></br><br></br>
          ‚ÄªÏ£ºÏùòÏÇ¨Ìï≠‚Äª<br></br>
          ‚ù§Ô∏èÏç∏ÎÑ§ÏùºÏùÄ ÏûêÏú† (Í∏∞ÏÇ¨Ïóê ÏóÜÎäî Îã§Î•∏ ÏÇ¨ÏßÑÏúºÎ°ú Ìï¥ÎèÑ Î¨¥Í¥Ä), Î†àÏù¥ÏïÑÏõÉ ÏÇ¨ÏßÑÏùÄ ÏßÄÏñë (FOR Ï†ÑÎ¨∏ÏÑ± FROM Ï†Ñ Ìé∏ÏßëÏû•Îãò)<br></br>
          ‚ù§Ô∏èÏç∏ÎÑ§ÏùºÏùÄ ÏóÖÎ°úÎìúÌïú ÏÇ¨ÏßÑ "ÎßÅÌÅ¨ Î≥µÏÇ¨"Ìï¥ÏÑú ÎßÅÌÅ¨ Î∂ôÏó¨ÎÑ£ÏúºÏãúÍ±∞ÎÇò, <br></br>
            Í∏∞ÏÇ¨Ïóê Ïò¨Î¶¨Í≥† Ïã∂ÏßÑ ÏïäÏùÄÎç∞ Ïç∏ÎÑ§ÏùºÎ°úÎßå ÎÑ£Í≥† Ïã∂ÏúºÏãúÎã§Î©¥ ÏïïÏ∂ï‚ñ∂ÏÇ¨ÏßÑÎ™ÖÏùÄ "173_3(1)_thumbnail" ÏñëÏãù‚ñ∂"ÏóÖÎ°úÎìú Î©îÎâ¥"‚ñ∂"ÌååÏùº ÏÑ†ÌÉù"‚ñ∂"ÏÑúÎ≤ÑÎ°ú Ï†ÑÏÜ°"ÍπåÏßÄÎßå(ÌôïÏù∏ Î≤ÑÌäº X)‚ñ∂ÎßÅÌÅ¨ Î≥µÎ∂ô<br></br>
          ‚ù§Ô∏èÏ†úÎ™©ÏùÄ contentÏπ∏Ïóê Îòê Ïì∞ÏßÄ ÏïäÍ∏∞!<br></br>
          ‚ù§Ô∏èÎ∂ôÏó¨ÎÑ£Í∏∞ Ìï† Îïå ctrl+ shift + v or "ÌÖçÏä§Ìä∏Î°ú Î∂ôÏó¨ÎÑ£Í∏∞" Î≤ÑÌäº<br></br>
          ‚ù§Ô∏èÌÉ≠ ÎåÄÏã† Ïä§ÌéòÏù¥Ïä§ 4Î≤à or "Îì§Ïó¨Ïì∞Í∏∞" Î≤ÑÌäº<br></br>
          ‚ù§Ô∏èÏóêÎîîÌÑ∞ Í≥µÍ∞Ñ Ïö∞Ï∏°ÌïòÎã® Ïä§ÌÅ¨Î°§Î°ú ÎäòÎ¶¨Í∏∞ Í∞ÄÎä•!<br></br>
        </p>
      </div>
      <div>
        <label>Content</label>
        <textarea
          id="editor"
          value={form.content}  // valueÎ°ú form.contentÎ•º ÏÑ§Ï†ï
          onChange={(e) => setForm({ ...form, content: e.target.value })} // onChangeÎ°ú ÏÉÅÌÉú Í¥ÄÎ¶¨
        />
      </div>
      <button onClick={handleSubmit} className="submit-button">
        Submit
      </button>
    </div>
  );
}

export default PostForm;
