import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./../styles/PostForm.css";

//게시글 작성
function PostForm() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: "", 
    category: "", 
    content: "", 
    issue: "", 
    page: "", 
    previewSentence: "", 
    thumbnailImage: "" ,
  });
  const editorRef = useRef(null); // CKEditor 인스턴스 참조

  useEffect(() => {
    // CKEditor 초기화
    const initializeEditor = () => {
      const editorElement = document.getElementById("editor");
      if (!editorElement) {
        console.error("Editor element not found!");
        return;
      }

      // CKEditor 초기화
      const editor = window.CKEDITOR.replace("editor", {
        height: 300,
        extraPlugins: "uploadimage",
        filebrowserUploadMethod: "form",
        filebrowserUploadUrl: "http://localhost:7000/api/posts/postImages",
      });

      // CKEditor 이벤트 설정
      editor.on("instanceReady", function () {
        console.log("CKEditor가 준비되었습니다.");
        editor.on("fileUploadRequest", function (evt) {
          const fileLoader = evt.data.fileLoader;
          const formData = new FormData(); // FormData 객체 생성
          formData.append("upload", fileLoader.file);  // 파일 추가

          const xhr = new XMLHttpRequest();  // XMLHttpRequest 객체 생성
          xhr.open("POST", "http://localhost:7000/api/posts/postImages", true);  // 업로드 URL로 요청
          xhr.setRequestHeader("Authorization", `Bearer ${localStorage.getItem("token")}`); // 헤더에 인증 토큰 추가

          // 파일 업로드 진행 시
          xhr.onload = function () {
            const response = JSON.parse(xhr.responseText);
            if (response.uploaded) {
              fileLoader.fileName = response.url;  // 업로드된 URL을 fileName에 저장
            } else {
              console.error("Upload failed:", response.error);  // 업로드 실패 시
            }
          };
          xhr.send(formData); // FormData를 전송

          evt.stop();  // 기본 이벤트를 멈춤
        });

        editorRef.current = editor; // CKEditor 인스턴스를 ref에 저장
      });
    };

    initializeEditor();

    return () => {
      if (editorRef.current) {
        editorRef.current.destroy(true); // 컴포넌트 언마운트 시 CKEditor 인스턴스 제거
      }
    };
  }, []);

  const handleSubmit = async () => {
    const content = editorRef.current?.getData(); // CKEditor 내용 가져오기
    try {
      const response = await fetch("http://localhost:7000/api/posts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ ...form, content }),
      });
      const data = await response.json();
      if (response.ok) {
        alert("게시글 작성이 완료되었습니다.");
        navigate("/");
      } else {
        alert(data.message || "게시글 제출 오류");
      }
    } catch (err) {
      console.error("게시글 제출 오류: ", err);
    }
  };


  return (
    <div className="post-form-container">
      <h1>Post Article</h1>
      <div>
        <label>Title</label>
        <input
          type="text"
          placeholder="기사 제목(편집일 최종본)"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
      </div>
      <div>
        <label>Issue</label>
        <input
          type="text"
          placeholder="몇 호인지 숫자만 입력 (예: 173)"
          value={form.issue}
          onChange={(e) => setForm({ ...form, issue: e.target.value })}
        />
      </div>
      <div>
        <label>Category</label>
        <select
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
        >
          <option>On Campus</option>
          <option>Feature</option>
          <option>Issue</option>
          <option>Society</option>
          <option>Global</option>
          <option>Culture</option>
          <option>Entertainment</option>
          <option>Economics</option>
          <option>Business</option>
          <option>Technology</option>
        </select>

        <label>Page (예: 3면(1)이면 31 선택)</label>
        <select
          value={form.page}
          onChange={(e) => setForm({ ...form, page: e.target.value })}
        >
